sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    // Modified the if to return if the newValue == oldValue to avoid
    // unecessary trips to the server
    if (isLoading || newValue === '' || newValue == oldValue) {
        return;
    }
alert ("hola voy por aqui" + oldValue  + " to " + newValue );
    // Instantiate the getNombre Script Include 
    var getNombreEmp = new GlideAjax('GetNombreEmpleado');
    // Specify the getEmail method

	getNombreEmp.addParam('sysparm_name','getNombre');
      // Pass the Requested for sys_id
    getNombreEmp.addParam('sysparm_userID', g_form.getValue('codigoempleado'));
	// Send the request to the server
    getNombreEmp.getXML(populateNombreEmp);
alert ("22hola voy por aqui>" );


    // When the response is back from the server
    function populateNombreEmp(response){
        // Extract the email address from the response, clear any value from the email field, 
        // set new value in the email field
        var NombreScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('u_nombreempleado');
        g_form.setValue('u_nombreempleado',NombreScriptInclude);
    }
}
